function View({ onClose }) {
    const handleClose = () => {
        onClose();
    };
    const modalStyle = {
        backgroundImage: 'url(https://images.unsplash.com/photo-1623600989906-6aae5aa131d4?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1582&q=80)',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        backgroundSize: 'cover'
    };

    return (
        <>
            <span className="close" onClick={handleClose}>
                &times;
            </span>

            <div className="min-w-screen h-screen animated fadeIn faster fixed left-0 top-0 flex justify-center items-center inset-0 z-50 outline-none focus:outline-none bg-no-repeat style={modalStyle} bg-center bg-cover">
                <div className="absolute bg-black opacity-80 inset-0 z-0"></div>
                <div className="w-2/3 p-5 relative mx-auto my-auto rounded-xl shadow-lg bg-white">
                    <div className="">

                        <div className="text-center p-5 flex-auto justify-center">

                            <h2 className="text-xl font-bold py-4 ">Are you sure?</h2>
                            <p className="text-sm text-gray-500 px-8">Do you really want to delete your account?
                                This process cannot be undone</p>
                        </div>

                        <div className="p-3  mt-2 text-center space-x-4 md:block">
                            <button
                                onClick={handleClose}
                                className="mb-2 md:mb-0 bg-white px-5 py-2 text-sm shadow-sm font-medium tracking-wider border text-gray-600 rounded-full hover:shadow-lg hover:bg-gray-100">
                                Cancel
                            </button>
                            <button className="mb-2 md:mb-0 bg-red-500 border border-red-500 px-5 py-2 text-sm shadow-sm font-medium tracking-wider text-white rounded-full hover:shadow-lg hover:bg-red-600">Delete</button>
                        </div>

                    </div>
                </div>
            </div>
        </>
    )
}
export default View
