import React, {useState} from "react"
import View  from "./View"
import Update from "./Update";

function Card(){
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(""); 
  const handleViewClick = () => {
    setIsModalOpen(true);
    setModalType("view");
  };

  const handleUpdateClick = () => {
    setIsModalOpen(true);
    setModalType("update");
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setModalType("");
  };


    return(

        <div className="max-w-4xl px-2 mr-40 py-2 bg-white rounded-lg shadow-md">
            <div>
                <a className="flex items-center" href="#">
                    <img className="mx-4 w-10 h-10 object-cover rounded-full hidden sm:block" src="https://images.unsplash.com/photo-1502980426475-b83966705988?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=373&q=80" alt="avatar"/>
                    <h1 className="text-gray-700 font-bold">Khatab wedaa</h1>
                </a>
            </div>
        <div className="flex justify-between items-center">
            <span className="font-light text-gray-600">mar 10, 2019</span>
            <a className="px-2 py-1 bg-gray-600 text-gray-100 font-bold rounded hover:bg-gray-500" href="#">Design</a>
        </div>
        <div className="mt-2">
            <a className="text-2xl text-gray-700 font-bold hover:text-gray-600" href="#">Accessibility tools for designers and developers</a>
            <p className="mt-2 text-gray-600">Lorem ipsum dolor sit, amet consectetur adipisicing elit. Tempora expedita dicta totam aspernatur doloremque. Excepturi iste iusto eos enim reprehenderit nisi, accusamus delectus nihil quis facere in modi ratione libero!</p>
        </div>
        <div className="flex mb-4 justify-between items-center mt-4">
            <a className="text-blue-600 hover:underline" href="#">Read more</a>

        </div>

        <div className="flex items-center justify-center ">
        <button
            type="button"
            className="inline-block mr-6  bg-blue-500 px-10 pb-2 pt-2.5 text-xs font-bold uppercase leading-normal text-white shadow-md transition duration-150 ease-in-out hover:bg-blue-600 hover:shadow-lg focus:bg-blue-600 focus:shadow-lg focus:outline-none focus:ring-2 focus:ring-blue-300 dark:bg-blue-700 dark:hover:bg-blue-800 dark:focus:bg-blue-800 dark:focus:ring-2 dark:focus:ring-blue-500"
            onClick={handleViewClick}
          >
            View 
          </button>


          <button
            type="button"
            onClick={handleUpdateClick}
            className="inline-block mr-6 bg-success-500 font-bold px-10 pb-2 pt-2.5 text-xs  uppercase leading-normal text-white shadow-md transition duration-150 ease-in-out hover:bg-yellow-600 hover:shadow-lg focus:bg-yellow-600 focus:shadow-lg focus:outline-none focus:ring-2 focus:ring-yellow-300 dark:bg-yellow-700 dark:hover:bg-yellow-800 dark:focus:bg-yellow-800 dark:focus:ring-2 dark:focus:ring-yellow-500"
          >
            Update
          </button>


          <button
            type="button"
            className="inline-block mr-6 bg-red-500 px-10 pb-2 pt-2.5 text-xs font-bold uppercase leading-normal text-white shadow-md transition duration-150 ease-in-out hover:bg-red-600 hover:shadow-lg focus:bg-red-600 focus:shadow-lg focus:outline-none focus:ring-2 focus:ring-red-300 dark:bg-red-700 dark:hover:bg-red-800 dark:focus:bg-red-800 dark:focus:ring-2 dark:focus:ring-red-500"
          >
            Delete
          </button>
        </div>
        {isModalOpen && modalType === "view" && <View onClose={handleCloseModal} />}
       {isModalOpen && modalType === "update" && <Update onClose={handleCloseModal} />}
    </div>



    )
    }

    export default Card
