import React, { useState } from "react"
import View from "./View"
import Update from "./Update";
import DeletePost from "./DeletePost";

function Card({ projects, loading, error, deleteProject}) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState("");
  const [currentProjectId, setCurrentProjectId] = useState(null);

  const handleViewClick = (projectId) => {
    setIsModalOpen(true);
    setCurrentProjectId(projectId)
    setModalType("view");
  };

  const handleUpdateClick = (projectId) => {
    setIsModalOpen(true);
    setCurrentProjectId(projectId)
    setModalType("update");
  };
  const handleDeleteClick = (projectId) => {
    setIsModalOpen(true);
    setCurrentProjectId(projectId)
    setModalType("delete");
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setModalType("");
  }


  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (Array.isArray(projects) && projects.length > 0) {
    return (
      <div className="max-w-4xl max-h-screen overflow-y-auto px-2 mr-4 mt-6 py-2 bg-white rounded-lg shadow-md">
        {projects.map((user) => (
          <li key={user.id} className="py-3 sm:py-4">
            <div>
              {/* Display user details */}
              <p className="font-light text-gray-600">{user.id}</p>
              <h1 className="text-gray-700 font-bold">{user.name}</h1>
              <p className="font-light text-gray-600">{user.description}</p>
              <span className="font-light text-gray-600">{user.category}</span>
            </div>
            <div className="flex mb-4 justify-between items-center mt-4">
              <a className="text-blue-600 hover:underline" href="#">Read more</a>
            </div>

            <div className="flex items-center justify-center">
              <button
                type="button"
                className="inline-block mr-6 bg-blue-500 px-10 pb-2 pt-2.5 text-xs font-bold uppercase leading-normal text-white shadow-md transition duration-150 ease-in-out hover:bg-blue-600 hover:shadow-lg focus:bg-blue-600 focus:shadow-lg focus:outline-none focus:ring-2 focus:ring-blue-300 dark:bg-blue-700 dark:hover:bg-blue-800 dark:focus:bg-blue-800 dark:focus:ring-2 dark:focus:ring-blue-500"
                onClick={() => handleViewClick(user.id)}
              >
                View
              </button>

              <button
                type="button"
                onClick={()=> handleUpdateClick(user.id)}
                className="inline-block mr-6 bg-green-500 font-bold px-10 pb-2 pt-2.5 text-xs uppercase leading-normal text-white shadow-md transition duration-150 ease-in-out hover:bg-yellow-600 hover:shadow-lg focus:bg-yellow-600 focus:shadow-lg focus:outline-none focus:ring-2 focus:ring-yellow-300 dark:bg-yellow-700 dark:hover:bg-yellow-800 dark:focus:bg-yellow-800 dark:focus:ring-2 dark:focus:ring-yellow-500"
              >
                Update
              </button>

              <button
                type="button"
                onClick={() => handleDeleteClick(user.id)}
                className="inline-block mr-6 bg-red-500 px-10 pb-2 pt-2.5 text-xs font-bold uppercase leading-normal text-white shadow-md transition duration-150 ease-in-out hover:bg-red-600 hover:shadow-lg focus:bg-red-600 focus:shadow-lg focus:outline-none focus:ring-2 focus:ring-red-300 dark:bg-red-700 dark:hover:bg-red-800 dark:focus:bg-red-800 dark:focus:ring-2 dark:focus:ring-red-500"
              >
                Delete
              </button>
            </div>
           </li>
            ))}
            {isModalOpen && modalType === "view" && <View  project_id={currentProjectId} onClose={handleCloseModal} />}
            {isModalOpen && modalType === "update" && <Update id ={currentProjectId} onClose={handleCloseModal} updateProject />}
            {isModalOpen && modalType === "delete" && <DeletePost id ={currentProjectId} onClose={handleCloseModal} deleteProject={deleteProject}/>}
      </div>
    );
  } else if (projects) {
    // Handle a single user investor object
    return (
      <div className="max-w-4xl max-h-screen overflow-y-auto px-2 mr-4 mt-6 py-2 bg-white rounded-lg shadow-md">
        {/* Display details for the single user investor */}
        <h1 className="text-gray-700 font-bold">{projects.name}</h1>
        <p className="font-light text-gray-600">{projects.description}</p>
        <span className="font-light text-gray-600">{projects.category}</span>
      </div>
    );
  } else {
    return <div>No user investors found.</div>;
  }







}

export default Card
